int main ( ) { int i , t , smallest , cost = 0 ; long long int n ; scanf ( "%d" , & t ) ; while ( t -- ) { cost = 0 ; scanf ( "%lld" , & n ) ; long int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%ld" , & a [ i ] ) ; } smallest = a [ 0 ] ; for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] < smallest ) { smallest = a [ i ] ; } } cost = ( n - 1 ) * smallest ; printf ( "%d\n" , cost ) ; } return 0 ; }