int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n , s = 0 ; scanf ( "%d" , & n ) ; int a [ n ] , i ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( i = 0 ; i < n - 1 ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { s = s + a [ i + 1 ] ; } else { s = s + a [ i ] ; } } printf ( "%d\n" , s ) ; } return 0 ; }