int main ( ) { int n1 , n2 , n , gcd , lcm , r , numer , denom ; scanf ( "%d" , & n ) ; for ( int i = 0 ; i < n ; i ++ ) { scanf ( "%d %d" , & n1 , & n2 ) ; if ( n1 > n2 ) { numer = n1 ; denom = n2 ; } else { numer = n2 ; denom = n1 ; } r = numer % denom ; while ( r != 0 ) { numer = denom ; denom = r ; r = numer % denom ; } gcd = denom ; lcm = n1 * n2 / gcd ; printf ( "%d %d\n" , lcm , gcd ) ; } return 0 ; }