int a [ 100 ] [ 100 ] ; int sumtrian ( int row , int col , int n ) ; int main ( ) { int i , j , n , sum ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j < i + 1 ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } sum = sumtrian ( 0 , 0 , n ) ; printf ( "%d" , sum ) ; return 0 ; } int sumtrian ( int row , int col , int n ) { int sum1 , sum2 ; if ( row == n - 1 ) { return a [ row ] [ col ] ; } else { sum1 = a [ row ] [ col ] + sumtrian ( row + 1 , col , n ) ; sum2 = a [ row ] [ col ] + sumtrian ( row + 1 , col + 1 , n ) ; } return sum1 > sum2 ? sum1 : sum2 ; }