int gcd ( ) ; int lcm ( ) ; int main ( ) { int n , a , b , c , i , d ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; c = gcd ( a , b ) ; d = lcm ( a , b ) ; printf ( "%d %d\n" , c , d ) ; } return 0 ; } int gcd ( int n , int m ) { if ( n % m == 0 ) { return m ; } else { gcd ( n = m , n % m ) ; } } int lcm ( int a , int b ) { int i , j , n , m , flag = 0 ; for ( i = 1 ; i <= 1000000 ; i ++ ) { n = a * i ; for ( j = 1 ; j <= 1000000 ; j ++ ) { m = b * j ; if ( n == m ) { flag = 1 ; break ; } } if ( flag == 1 ) { break ; } } return m ; }