main ( ) { int gcd , t , a , b , i , lcm ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; for ( i = 1 ; i <= a && i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) gcd = i ; } lcm = a * b / gcd ; printf ( "%d" , gcd ) ; printf ( " %d" , lcm ) ; } }