int a [ 5050 ] ; int main ( ) { int t = 0 , s = 0 , i = 0 , j = 0 , n = 0 , tempn = 0 ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; s = n * ( n + 1 ) / 2 ; for ( i = 0 ; i < s ; i ++ ) scanf ( "%d" , & a [ i ] ) ; j = s - n - 1 ; tempn = n ; while ( -- n ) { for ( i = 1 ; i < tempn ; i ++ ) { a [ j ] += ( a [ j + tempn ] > a [ j + tempn - 1 ] ? a [ j + tempn ] : a [ j + tempn - 1 ] ) ; j -- ; } tempn -- ; } printf ( "%d\n" , a [ 0 ] ) ; } return 0 ; }