int main ( ) { int t , a = 0 , b = 0 , c = 0 , e , f ; scanf ( "%d" , & t ) ; int gcd [ t ] , lcm [ t ] ; while ( a < t ) { int i = 1 ; scanf ( "%d %d" , & e , & f ) ; int j = ( e > f ) ? f : e ; int x = ( e > f ) ? e : f ; int z = e * f ; while ( i <= j ) { if ( e % i == 0 && f % i == 0 ) { gcd [ a ] = i ; } ++ i ; } while ( j <= z ) { if ( j % e == 0 && j % f == 0 ) { lcm [ a ] = j ; break ; } ++ j ; } a ++ ; } while ( c < t ) { printf ( "%d %d\n" , gcd [ c ] , lcm [ c ] ) ; ++ c ; } return 0 ; }