int Call ( int x , int y ) { int a , b , t , gc , lc ; a = x ; b = y ; if ( b == 0 ) { return 0 ; } while ( b != 0 ) { t = b ; b = a % b ; a = t ; } gc = a ; lc = ( x * y ) / gc ; printf ( "%d %d\n" , gc , lc ) ; } int main ( void ) { int n ; long A , B , gcd , lcm ; scanf ( "%d" , & n ) ; for ( int i = 0 ; i < n ; i ++ ) { scanf ( "%l,%l" , & A , & B ) ; Call ( A , B ) ; } return 0 ; }