int main ( ) { int A [ 101 ] [ 101 ] ; int sum_prev , i , j , k , sum , col , t , n , max ; scanf ( "%d" , & t ) ; for ( k = 0 ; k < t ; k ++ ) { scanf ( "%d" , & n ) ; max = 0 ; for ( i = 1 ; i <= n ; i ++ ) { for ( j = 1 ; j <= i ; j ++ ) { scanf ( "%d" , & A [ i ] [ j ] ) ; if ( j == 1 ) { max = max + A [ i ] [ j ] ; } } } sum_prev = 0 ; for ( i = 2 ; i <= n ; i ++ ) { sum_prev = sum_prev + A [ i - 1 ] [ i - 1 ] ; for ( j = i ; j <= n ; j ++ ) { sum = sum_prev ; col = i - 1 ; for ( k = 2 ; k <= n ; k ++ ) { if ( k == j ) { col = col + 1 ; } sum = sum + A [ k ] [ col ] ; } printf ( "sum = %d\n" , sum ) ; if ( sum > max ) { max = sum ; } } } printf ( "%d\n" , max ) ; } return 0 ; }