int main ( ) { int t , sum [ 100 ] , rows , a , i , j , k , max ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & rows ) ; for ( j = 0 ; j < rows ; j ++ ) { sum [ j ] = 0 ; for ( k = 0 ; k <= j ; k ++ ) { scanf ( "%d" , & a ) ; sum [ j ] += a ; } } max = sum [ 0 ] ; for ( j = 0 ; j < rows ; j ++ ) { if ( max < sum [ j ] ) max = sum [ j ] ; } printf ( "%d\n" , max ) ; } return 0 ; }