int main ( ) { int T ; scanf ( "%d" , & T ) ; while ( T -- ) { long int num1 , num2 , i , min , max ; scanf ( "%ld %ld" , & num1 , & num2 ) ; if ( num1 > num2 ) min = num2 ; max = num1 ; for ( i = min ; i > 0 ; i -- ) { if ( num1 % i == 0 && num2 % i == 0 ) { printf ( "%ld " , i ) ; } } for ( i = 1 ; i <= num1 * num2 ; i ++ ) { if ( i % num1 == 0 && i % num2 == 0 ) { printf ( "%ld\n" , i ) ; } } } return 0 ; }