int main ( ) { int T , i , a , sum [ 100 ] , max = 0 , m = 0 , n , k , j ; scanf ( "%d" , & T ) ; for ( i = 0 ; i < T ; i ++ ) { scanf ( "%d" , & a ) ; for ( j = 1 ; j <= a ; j ++ ) { for ( k = 1 ; k <= j ; k ++ ) { scanf ( "%d" , & n ) ; sum [ m ] = sum [ m ] + n ; } m ++ ; } } for ( i = 0 ; i <= a - 1 ; i ++ ) { if ( sum [ i ] > sum [ i + 1 ] ) ; max = sum [ i ] ; } printf ( "%d" , max ) ; return 0 ; }