int main ( ) { int t , i , j ; long n , * a , min , * result ; scanf ( "%d" , & t ) ; result = malloc ( sizeof ( long ) * t ) ; for ( i = 1 ; i <= t && i <= 10 ; i ++ ) { scanf ( "%ld" , & n ) ; a = malloc ( sizeof ( long ) * n ) ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%ld" , & a [ j ] ) ; for ( j = 0 , min = a [ 0 ] ; i < n ; j ++ , n -- ) if ( min > a [ j ] && min > a [ n - 1 ] ) if ( a [ j ] < a [ n - 1 ] ) min = a [ j ] ; else min = a [ n - 1 ] ; result [ i - 1 ] = min ; } for ( i = 0 ; i < t && i < 10 ; i ++ ) printf ( "%ld\n" , result [ i ] ) ; return ( 0 ) ; }