int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long a , b , i , lcm , gcd ; scanf ( "%ld %ld" , & a , & b ) ; lcm = a * b * 1.0 ; for ( i = 1 ; i <= a && i <= b ; ++ i ) { if ( a % i == 0 && b % i == 0 ) gcd = i ; } lcm = ( a * b ) / gcd ; printf ( "%ld %ld\n" , gcd , lcm ) ; } return 0 ; }