int main ( ) { int n , i , j , max , sum = 0 , flag , t , no ; scanf ( "%d" , & t ) ; if ( t <= 1000 ) { while ( t != 0 ) { scanf ( "%d" , & n ) ; if ( n > 0 && n < 100 ) { int a [ n ] [ n ] ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & no ) ; if ( no >= 0 && no <= 99 ) a [ i ] [ j ] = no ; else exit ( 0 ) ; } } max = a [ 0 ] [ 0 ] ; sum = a [ 0 ] [ 0 ] ; for ( i = 0 ; i < n - 1 ; i ++ ) { flag = i ; for ( j = 0 ; flag == i ; j ++ ) { sum = a [ i ] [ j ] + a [ i + 1 ] [ j ] + a [ i + 1 ] [ j + 1 ] ; flag ++ ; if ( sum > max ) { max = sum ; } } } printf ( "%d\n" , max ) ; t -- ; } else exit ( 0 ) ; } } return 0 ; }