main ( ) { int a [ 100 ] , b [ 100 ] , i , j , e , c , d , k , t , n , f ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { c = d = e = 0 ; scanf ( "%d" , & n ) ; for ( k = 0 ; k < n ; k ++ ) { for ( j = 0 ; j < k + 1 ; j ++ ) { scanf ( "%d" , & c ) ; if ( j == 0 ) b [ j ] = c + a [ j ] ; else { d = c + a [ j ] ; e = c + a [ j - 1 ] ; if ( d >= e ) b [ j ] = d ; else b [ j ] = e ; } } for ( j = 0 ; j < k ; j ++ ) a [ j ] = b [ j ] ; } f = a [ 0 ] ; for ( j = 0 ; j < n - 1 ; j ++ ) { if ( f < a [ j + 1 ] ) f = a [ j + 1 ] ; } printf ( "%d\n" , f ) ; } return ( 0 ) ; }