int main ( ) { int t , gcd , lcm , a , b , s , c , k , n1 , n2 , p ; scanf ( "%d" , & t ) ; while ( t != 0 ) { scanf ( "%d%d" , & a , & b ) ; n1 = a , n2 = b , k = 2 ; while ( k != 0 ) { s = a % b ; printf ( "remi=%d\n" , s ) ; if ( s == 0 ) { gcd = b ; k = 0 ; } else { c = b ; b = s ; a = c ; } } p = n1 * n2 ; lcm = ( n1 * n2 ) / gcd ; printf ( "%d %d\n" , gcd , p ) ; t = t - 1 ; } return 0 ; }