typedef long long int ll ; int GCD ( ll a , ll b ) { if ( b == 0 ) return a ; return GCD ( b , a % b ) ; } int LCM ( ll a , ll b ) { return b * a / GCD ( a , b ) ; } int main ( ) { long long int a , b ; int t ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%lld %lld" , & a , & b ) ; printf ( "%lld " , GCD ( a , b ) ) ; printf ( "%lld " , LCM ( a , b ) ) ; printf ( "\n" ) ; } return 0 ; }