int main ( ) { long int t , n , i , j ; long long int r , c ; scanf ( "%d" , & t ) ; while ( t -- ) { c = 0 ; scanf ( "%ld" , & n ) ; long long int a [ n ] ; for ( i = 1 ; i <= n ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; c ++ ; } for ( i = 1 ; i < n ; i ++ ) { r = a [ i ] ; for ( j = i + 1 ; j <= n ; j ++ ) { if ( r < a [ j ] ) { c ++ ; r = a [ j ] ; } } } printf ( "%ld\n" , c ) ; } return 0 ; }