int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int n , i , j , max = 0 ; scanf ( "%lld" , & n ) ; long long int a [ n ] [ n ] ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%lld" , & a [ i ] [ j ] ) ; } } for ( i = 1 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { a [ i ] [ j ] += a [ i - 1 ] [ j / 2 ] ; } } max = a [ n - 1 ] [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ n - 1 ] [ i ] >= max ) max = a [ n - 1 ] [ i ] ; } printf ( "%lld\n" , max ) ; } return 0 ; }