int main ( ) { int i = 0 , j = 0 , k = 0 , tc = 0 , a_s = 0 , cost = 0 ; scanf ( "%d" , & tc ) ; for ( i = 0 ; i < tc ; i ++ ) { scanf ( "%d" , & a_s ) ; int arr [ a_s ] ; for ( j = 0 ; j < a_s ; j ++ ) scanf ( "%d" , & arr [ j ] ) ; for ( k = 0 ; k < ( a_s - 1 ) ; k ++ ) { if ( arr [ k ] >= arr [ k + 1 ] ) cost = cost + arr [ k + 1 ] ; else { cost = cost + arr [ k ] ; arr [ k + 1 ] = arr [ k ] ; } } printf ( "%d\n" , cost ) ; cost = 0 ; } return 0 ; }