int main ( ) { int t , a , b , min , max , i , gcd , lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & a , & b ) ; if ( a < b ) { min = a ; max = b ; } else { min = b ; max = a ; } for ( i = 1 ; i <= min ; i ++ ) if ( a % i == 0 && b % i == 0 ) gcd = i ; for ( i = max ; i <= a * b ; i ++ ) if ( i % a == 0 && i % b == 0 ) { lcm = i ; break ; } printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }