main ( ) { int test , i , a , b ; int gcd ( int a , int b ) ; scanf ( "%d" , & test ) ; long unsigned int lcm [ test ] , hcf [ test ] ; for ( i = 0 ; i < test ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; hcf [ i ] = gcd ( a , b ) ; lcm [ i ] = a * b / hcf [ i ] ; } for ( i = 0 ; i < test ; i ++ ) printf ( "%lu %lu\n" , hcf [ i ] , lcm [ i ] ) ; } int gcd ( int x , int y ) { if ( x % y == 0 ) return y ; else return gcd ( y , x % y ) ; }