int gcd ( int a , int b ) { int g ; if ( a > b ) { if ( a % b == 0 ) g = b ; else g = gcd ( b , a % b ) ; } else { if ( b % a == 0 ) g = a ; else g = gcd ( a , b % a ) ; } return g ; } int least ( int a , int b ) { int l , g ; g = gcd ( a , b ) ; l = ( a / g * b / g ) ; return l ; } int main ( ) { int t , i , j , a , b ; scanf ( "%d" , & t ) ; int hcf [ t ] , lcm [ t ] ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; hcf [ i ] = gcd ( a , b ) ; lcm [ i ] = least ( a , b ) ; } for ( i = 0 ; i < t ; i ++ ) { printf ( "%d %d\n" , hcf [ i ] , lcm [ i ] ) ; } return 0 ; }