int main ( ) { int t , n1 , n2 , GCD = 1 , LCM , i , j , k ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d%d" , & n1 , & n2 ) ; for ( j = 2 ; j <= n1 ; j ++ ) { if ( n1 % j == 0 && n2 % j == 0 ) { GCD = j ; continue ; } } printf ( "%d " , GCD ) ; for ( k = n1 || n2 ; k < ( n1 * n2 ) ; k ++ ) { if ( k % n1 == 0 && k % n2 == 0 ) { LCM = k ; break ; } else { LCM = n1 * n2 ; } } printf ( "%d\n" , LCM ) ; } return 0 ; }