int main ( ) { int t ; long long int i , a , b , p , gcd ; scanf ( "%d" , & t ) ; if ( 1 <= t <= 1000 ) { while ( t -- ) { scanf ( "%lld %lld" , & a , & b ) ; if ( 1 <= a <= 1000000 && 1 <= b <= 1000000 ) { if ( b >= a ) { int p = a ; a = b ; b = p ; } for ( i = 1 ; i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { gcd = i ; } } } printf ( "%lld\n" , gcd ) ; printf ( "%lld\n" , a * b / gcd ) ; } } }