int main ( ) { int t ; int callcm ( int , int ) ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b , i , gcd = 1 , lcm ; scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; for ( i = 2 ; i <= a && i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) gcd *= i ; } lcm = callcm ( a , b ) ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; } int callcm ( int a , int b ) { if ( a > b ) { int c = a ; while ( 1 ) { if ( a % b == 0 ) return a ; else a += c ; } } else { int c = b ; while ( 1 ) { if ( b % a == 0 ) return b ; else b += c ; } } }