int main ( ) { int t , i , min , n ; long int c ; scanf ( "%d" , & t ) ; while ( t > 0 ) { c = 0 ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } min = a [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] < min ) min = a [ i ] ; } c = ( n - 1 ) * min ; printf ( "%ld\n" , c ) ; t -- ; } return 0 ; }