int main ( void ) { int a ; long int b , c , gcd , temp , big , small , lcm ; scanf ( "%d" , & a ) ; while ( a -- ) { scanf ( "%ld %ld" , & b , & c ) ; if ( b >= c ) { big = b ; small = c ; } else { big = c ; small = b ; } for ( temp = small ; temp > 0 ; temp -- ) { if ( ( ( small % temp ) == 0 ) && ( big % temp ) == 0 ) { gcd = temp ; break ; } } lcm = b * c / gcd ; printf ( "%ld %ld\n" , gcd , lcm ) ; } return 0 ; }