int main ( ) { int t , l ; scanf ( "%d" , & t ) ; for ( l = 0 ; l < t ; l ++ ) { int n , i , j , k , g ; scanf ( "%d" , & n ) ; j = ( ( n + 1 ) * n ) / 2 ; int a [ j ] ; for ( i = 0 ; i < j ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } int vans = a [ 0 ] , dans = a [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { k = ( i * ( i + 1 ) ) / 2 ; g = k + i ; vans = vans + a [ k ] ; dans = dans + a [ g ] ; } if ( vans >= dans ) { printf ( "%d\n" , vans ) ; } else { printf ( "%d\n" , dans ) ; } } }