int main ( ) { int t , n , i , j , k , max ; int * a ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & k ) ; n = k * ( k + 1 ) / 2 ; a = ( int * ) malloc ( n * sizeof ( int ) ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } max = 0 ; for ( i = 0 ; i < n - 1 ; i ++ ) { for ( j = 1 ; j < n ; j ++ ) { k = a [ i ] + a [ j ] ; if ( k > max ) { max = k ; } } } printf ( "%d\n" , max ) ; } return 0 ; }