int main ( ) { int t ; int n ; int m ; int a ; int b ; int temp ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; scanf ( "%d" , & m ) ; if ( n > m ) { temp = m ; m = n ; n = temp ; } a = n ; b = m ; while ( n > 1 ) { if ( m % n == 0 ) { break ; } temp = m % n ; m = n ; n = temp ; } temp = b ; while ( b % a != 0 ) { b = b + temp ; } printf ( "%d %d\n" , n , b ) ; } return 0 ; }