long int gcdf ( long int , long int ) ; long int lcmf ( long int , long int ) ; int main ( ) { long int t , a , b , i , lc , gc ; scanf ( "%ld" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%ld %ld" , & a , & b ) ; lc = lcmf ( a , b ) ; gc = gcdf ( a , b ) ; printf ( "%ld %ld\n" , gc , lc ) ; } } long int gcdf ( long int m , long int n ) { long int temp ; if ( m < n ) { temp = m ; m = n ; n = temp ; } while ( ( m % n ) != 0 ) { temp = m ; m = n ; n = temp % n ; } return n ; } long int lcmf ( long int m , long int n ) { long int temp , d = 1 , k ; if ( m < n ) { temp = m ; m = n ; n = temp ; } d = 1 ; k = m ; while ( k % n != 0 ) { d ++ ; k = m * d ; } return k ; }