int arr [ 100 ] [ 100 ] ; int main ( ) { int i , j , t , n , x , s , k , l ; printf ( "Enter t\n" ) ; scanf ( "%f" , & t ) ; while ( t -- ) { printf ( "Enter n\n" ) ; scanf ( "%d" , & n ) ; if ( n > 0 && n < 100 ) { for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & arr [ i ] [ j ] ) ; } } s = arr [ 0 ] [ 0 ] ; l = 0 ; for ( k = 1 ; k < n ; k ++ ) { if ( arr [ k ] [ l ] > arr [ k ] [ l + 1 ] ) { x = l ; } else x = l + 1 ; s += arr [ k ] [ l ] ; l = x ; } printf ( "%d\n" , s ) ; } } return 0 ; }