int gcd ( int num1 , int num2 ) { int rem ; rem = num1 % num2 ; while ( rem != 0 ) { num1 = num2 ; num2 = rem ; rem = num1 % num2 ; } return num2 ; } int lcm ( int num1 , int num2 ) { int n = gcd ( num1 , num2 ) ; return num1 * num2 / n ; } int main ( ) { int t , num1 , num2 ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & num1 , & num2 ) ; printf ( "%d %d\n" , gcd ( num1 , num2 ) , lcm ( num1 , num2 ) ) ; } return 0 ; }