int main ( ) { int arr [ 100 ] [ 100 ] ; int i , j , tc , go , row , k ; long sum = 0 , max = 0 ; scanf ( "%d" , & tc ) ; for ( go = 1 ; go <= tc ; go ++ ) { scanf ( "%d" , & row ) ; for ( i = 0 ; i < row ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & arr [ i ] [ j ] ) ; } } for ( i = 0 ; i < row ; i ++ ) { sum = 0 ; k = i ; for ( j = i ; k < row ; k ++ ) { sum = sum + arr [ k ] [ j ] ; } sum = sum + arr [ k - 1 ] [ j + 1 ] ; if ( sum > max ) max = sum ; } printf ( "%ld" , max ) ; } return 0 ; }