int main ( ) { int * ptr , T , i , lcm , hcf , j , a , b ; scanf ( "%d" , & T ) ; ptr = ( int * ) malloc ( 2 * T * sizeof ( int ) ) ; for ( i = 0 ; i < T ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; if ( a < b ) { for ( j = a ; j <= a * b ; j ++ ) { if ( j % a == 0 && j % b == 0 ) break ; } lcm = j ; hcf = ( a * b ) / j ; } else { for ( j = b ; j < a * b ; j ++ ) { if ( j % a == 0 && j % b == 0 ) break ; } lcm = j ; hcf = ( a * b ) / j ; } ptr [ 2 * i ] = lcm ; ptr [ 2 * i + 1 ] = hcf ; } for ( i = 0 ; i < T ; i ++ ) printf ( "%d %d\n" , ptr [ 2 * i ] , ptr [ 2 * i + 1 ] ) ; return 0 ; }