int main ( void ) { unsigned long long int t , n , q , k = 1 ; unsigned long long int sum = 1 , current , element ; scanf ( "%llu" , & t ) ; for ( q = t ; q > 0 ; q -- ) { scanf ( "%llu" , & n ) ; scanf ( "%llu" , & current ) ; unsigned long long int i ; for ( i = 0 ; i < n - 1 ; i ++ ) { scanf ( "%llu" , & element ) ; if ( element >= current ) { k ++ ; current = element ; } else { k = 1 ; current = element ; } sum = sum + k ; } printf ( "%llu\n" , sum ) ; sum = 1 ; k = 1 ; } return 0 ; }