int main ( ) { int tc , a [ 10000 ] , ans [ 1000 ] ; int i , j , k , r , p , q , suml , sumr , it = 1 , flag ; scanf ( "%d" , & tc ) ; for ( i = 0 ; i < tc ; i ++ ) { r = 0 ; scanf ( "%d" , & a [ 0 ] ) ; for ( j = 1 ; j <= a [ 0 ] ; j ++ ) for ( k = 0 ; k < j ; k ++ ) scanf ( "%d" , & a [ ++ r ] ) ; flag = 1 ; it = 1 ; while ( flag ) { suml = 0 ; sumr = 0 ; for ( p = 1 ; p <= it ; p ++ ) suml += a [ p ] ; for ( q = r ; q > it ; q -- ) sumr += a [ q ] ; it ++ ; if ( suml >= sumr ) { flag = 0 ; ans [ i ] = suml ; } } } for ( i = 0 ; i < tc ; i ++ ) printf ( "\n%d" , ans [ i ] ) ; return 0 ; }