int gcd ( int a , int b ) { if ( a == 0 || b == 0 ) return 0 ; if ( a == b ) return a ; if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } int main ( void ) { int t ; scanf ( "%d" , & t ) ; for ( int i = 0 ; i < t ; i ++ ) { int a , b , mul , result = 0 ; scanf ( "%d%d" , & a , & b ) ; result = gcd ( a , b ) ; mul = a * b ; printf ( "%d %d\n" , result , ( mul / result ) ) ; } return 0 ; }