int main ( ) { int t , a , b , tem , c1 , c2 , f , temp , q1 , q2 ; scanf ( "%d" , & t ) ; while ( t -- ) { f = 1 ; scanf ( "%d%d" , & a , & b ) ; if ( b > a ) { temp = a ; a = b ; b = temp ; } tem = b ; while ( tem > 0 ) { c1 = b % tem ; if ( c1 == 0 ) { c2 = a % tem ; if ( c2 == 0 ) f = 0 ; } if ( f == 0 ) break ; tem -- ; } printf ( "%d %d" , tem , ( a * b ) / tem ) ; printf ( "\n" ) ; } }