int main ( void ) { int t , n ; scanf ( "%d\n" , & t ) ; for ( int i = 0 ; i < t ; i ++ ) { scanf ( "%d\n" , & n ) ; int a [ n ] ; for ( int j = 0 ; j < n ; j ++ ) { scanf ( "%d " , & a [ j ] ) ; } int max1 = a [ 0 ] ; for ( int k = 0 ; k < n ; k ++ ) { if ( a [ k ] > max1 ) { max1 = a [ k ] ; } } int max2 = a [ 0 ] ; for ( int l = 1 ; l < n ; l ++ ) { if ( a [ l ] > max2 && a [ l ] < max1 ) { max2 = a [ l ] ; } } printf ( "%d\n" , max2 ) ; } return 0 ; }