int main ( void ) { int n , b ; scanf ( "%d" , & n ) ; while ( n -- ) { scanf ( "%d" , & b ) ; int a [ b ] , j , k , count = 0 ; for ( j = 0 ; j < b ; j ++ ) { scanf ( "%d" , & a [ j ] ) ; } for ( k = 0 ; k < b - 1 ; k ++ ) { if ( a [ k ] > a [ k + 1 ] ) { count = count + a [ k + 1 ] ; } else if ( a [ k ] <= a [ k + 1 ] ) { count = count + a [ k ] ; } } printf ( "%d\n" , count ) ; count = 0 ; } return ( 0 ) ; }