int Gcd ( int a , int b ) { if ( b == 0 ) { return a ; } else { Gcd ( b , a % b ) ; } } int main ( ) { int k , a , b , i , j , c , l ; scanf ( "%d" , & k ) ; while ( k -- ) { scanf ( "%d%d" , & b , & a ) ; if ( a < b ) { l = Gcd ( b , a ) ; } else { l = Gcd ( a , b ) ; } if ( l == 1 ) { c = b * a ; } else { c = l * ( b / l ) * ( a / l ) ; } printf ( "%d %d\n" , l , c ) ; } return 0 ; }