long gcd ( long a , long b ) { if ( a == b ) return a ; else if ( a % b == 0 ) return b ; else if ( b % a == 0 ) return a ; else if ( a > b ) return gcd ( a % b , b ) ; else if ( a < b ) return gcd ( a , b % a ) ; } int main ( ) { int n , i ; long a , b ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%ld %ld" , & a , & b ) ; long g = gcd ( a , b ) ; long l = ( a * b ) / g ; printf ( "%ld %ld\n" , g , l ) ; } return 0 ; }