void main ( ) { short int t ; long * a , N , i , cost , minimum ; scanf ( "%d" , & t ) ; while ( t -- ) { cost = 0 ; scanf ( "%d" , & N ) ; a = ( long * ) malloc ( N * sizeof ( long ) ) ; for ( i = 0 ; i < N ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < N - 1 ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { minimum = a [ i + 1 ] ; cost += minimum ; a [ i + 1 ] = minimum ; } else { minimum = a [ i ] ; cost += minimum ; a [ i + 1 ] = minimum ; } } printf ( "%d\n" , cost ) ; free ( a ) ; } return ; }