void main ( ) { int t , i , j , c ; scanf ( "%d" , & t ) ; while ( t -- ) { int n , w = 0 ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n ; i ++ ) { c = i ; w ++ ; for ( j = i + 1 ; j < n ; j ++ ) { if ( a [ c ] >= a [ j ] ) break ; else c = j ; w ++ ; } } printf ( "%d\n" , w ) ; } }