int main ( ) { int t , a , b , x , i , j ; scanf ( "%d" , & t ) ; int gcd [ t ] , lcm [ t ] ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; lcm [ i ] = a * b ; if ( a == 0 ) gcd [ i ] = a ; else if ( b == 0 ) gcd [ i ] = b ; else { for ( j = 0 ; b != 0 ; j ++ ) { x = b ; b = a % b ; a = x ; } gcd [ i ] = a ; } lcm [ i ] = lcm [ i ] / gcd [ i ] ; printf ( "\n" ) ; printf ( "%d %d\n" , gcd [ i ] , lcm [ i ] ) ; } }