long gcd ( long , long ) ; int i , ans ; int main ( ) { int i , n , x , y , hcf ; scanf ( "%d" , & n ) ; long a [ n ] , b [ n ] , lcm ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & x ) ; scanf ( "%d" , & y ) ; hcf = gcd ( x , y ) ; lcm = ( x * y ) / hcf ; a [ i ] = hcf ; b [ i ] = lcm ; } for ( i = 0 ; i < n ; i ++ ) printf ( "%ld %ld\n" , a [ i ] , b [ i ] ) ; return 0 ; } long gcd ( long x , long y ) { if ( x == 0 ) return 0 ; for ( i = 0 ; i <= x ; i ++ ) { if ( x % i == 0 && y % i == 0 ) ans = i ; } return ans ; }